/// <reference lib="webworker" />
import { precacheAndRoute, cleanupOutdatedCaches, createHandlerBoundToURL } from 'workbox-precaching';
import { NavigationRoute, registerRoute } from 'workbox-routing';

declare let self: ServiceWorkerGlobalScope;

// Log SW activation
console.log('[SW] Service Worker initializing...');

// Clean old caches
cleanupOutdatedCaches();

// Precache all assets generated by the build
precacheAndRoute(self.__WB_MANIFEST);

// iOS PWA fix: Always serve index.html for navigation requests
// This prevents the white screen issue when reopening the PWA
const handler = createHandlerBoundToURL('/index.html');
const navigationRoute = new NavigationRoute(handler, {
  // Don't intercept API calls or static assets
  denylist: [
    /^\/api\//,
    /\.(js|css|png|jpg|jpeg|gif|svg|ico|woff|woff2)$/,
  ],
});
registerRoute(navigationRoute);

// Force immediate activation - CRITICAL for iOS
self.addEventListener('install', () => {
  console.log('[SW] Installing...');
  self.skipWaiting();
});

self.addEventListener('activate', (event) => {
  console.log('[SW] Activating...');
  event.waitUntil(
    Promise.all([
      self.clients.claim(),
      // Aggressively clear ALL caches to fix iOS white screen
      caches.keys().then((cacheNames) => {
        return Promise.all(
          cacheNames.map((cacheName) => {
            console.log('[SW] Deleting cache:', cacheName);
            return caches.delete(cacheName);
          })
        );
      }),
    ])
  );
});

// Push notification handler - CRITICAL for Safari iOS
self.addEventListener('push', (event: PushEvent) => {
  console.log('[SW] ====== PUSH EVENT RECEIVED ======');
  
  // Safari iOS requires simpler notification options
  let title = 'Nuova Notifica';
  let body = 'Hai ricevuto una nuova notifica';
  let data = {};

  if (event.data) {
    try {
      const payload = event.data.json();
      console.log('[SW] Payload:', JSON.stringify(payload));
      title = payload.title || title;
      body = payload.body || body;
      data = payload.data || {};
    } catch (e) {
      console.log('[SW] Text fallback:', event.data.text());
      body = event.data.text();
    }
  }

  // Safari iOS needs minimal options - no badge, no tag, no icon path issues
  const showPromise = self.registration.showNotification(title, {
    body: body,
    data: data
  });

  event.waitUntil(showPromise);
});

// Notification click handler
self.addEventListener('notificationclick', (event: NotificationEvent) => {
  console.log('[SW] Notification clicked');
  event.notification.close();

  const urlToOpen = (event.notification.data as { url?: string })?.url || '/';

  event.waitUntil(
    self.clients.matchAll({ type: 'window', includeUncontrolled: true }).then((clientList) => {
      for (const client of clientList) {
        if (client.url.includes(self.location.origin) && 'focus' in client) {
          (client as WindowClient).navigate(urlToOpen);
          return (client as WindowClient).focus();
        }
      }
      return self.clients.openWindow(urlToOpen);
    })
  );
});

self.addEventListener('notificationclose', () => {
  console.log('[SW] Notification closed');
});
